function MyComponent() {
 const [count, setCount] = useState();
 useEffect(() => {
 setCount(count + 1);
 }, []);
 return <div>{count}</div>;
}

function Greet({ name = "John" }) {
 return <h1>Hello {name}</h1>;
 }
 ReactDOM.render(<Greet name="Doe" />, document.getElementById('root'));

